import React from 'react';
import classes from './Modal.module.css';

const Modal = ({ reservation, selectedStatus, handleStatusChange, closeModal }) => {
  return (
    <div className={classes.modalBackdrop} onClick={closeModal}>
      <div className={classes.modalContent} onClick={(e) => e.stopPropagation()}>
        <h2>{reservation.nombre}</h2>
        <p>Fecha de Ingreso: {reservation.fechaingreso}</p>
        <p>Precio: {reservation.precio}</p>
        <p>Número de Personas: {reservation.numeropersonas}</p>
        <p>Descripción: {reservation.descripcion}</p>
        <div className={classes.buttonGroup}>
          <button onClick={() => handleStatusChange('pendiente')} className={selectedStatus === 'pendiente' ? classes.active : ''}>
            Pendiente
          </button>
          <button onClick={() => handleStatusChange('expirado')} className={selectedStatus === 'expirado' ? classes.active : ''}>
            Expirado
          </button>
          <button onClick={() => handleStatusChange('resuelto')} className={selectedStatus === 'resuelto' ? classes.active : ''}>
            Resuelto
          </button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
